
this.checkObjectPathExists = function(scope, path){
  
  if(!scope){
    throw "scope is null or undefined when checking object path.";
  }
  
  if(!path || !path.length ||  path.length < 1){
    throw "path undefined when checking object path.";
  }  
  
  var pathParts = path.split('.');
  
  for(var i = 0; i < pathParts.length; i++){
    if(scope[pathParts[i]] === undefined){
      return false;
    }
    
    scope = scope[pathParts[i]];
  }
  
  return true;
};

this.toSlug =  function(s){
    if(!s){
      return '';
    }
    var slug;
    slug = s.replace(/[^a-z0-9A-Z]/g, '-');
    slug = slug.replace(/-+/g, '-').toLowerCase();
    return slug;
};